import axios from "axios";
import React, { useEffect, useState } from "react";

import { Link } from "react-router-dom";

const Quote= () => {
  const [quote, setQuote] = useState();
  const [character, setCharacter] = useState();

  useEffect(() => {
    const headers = {
      Accept: "application/json",
      Authorization: "Bearer daNXH8aks1JeT2dEenRY",
    };
    (async function() {
      const rawQuotes = await axios.get("https://the-one-api.dev/v2/quote", {
        headers: headers,
      });

      const quote =
        rawQuotes.docs[Math.floor(Math.random() * rawQuotes.docs.length)];
      setQuote(quote.dialog);
      console.log(quote.dialog);
      const rawCharacters = await axios.get("https://the-one-api.dev/v2/character?_id=" 
      + quote.character, { headers: headers }
      );
      const character = rawCharacters.docs[0];
      console.log(character.name);
      setCharacter(character.name);
    }

   )()
  }, [])

  return (
    <div>
      <Link to="/character">
        <blockquote>{quote}</blockquote>
        <cite>- {character}</cite>
      </Link>
    </div>
  );
};

export default Quote
