import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Quote= () => {
   const [quote, setQuote] = useState();
   const [character, setCharacter] = useState();

   useEffect(() => {
     const headers = {
       Accept: "application/json",
       Authorization: "Bearer daNXH8aks1JeT2dEenRY",
     };
     const fetchData = async () => {
       const rawQuotes = await axios.get("https://the-one-api.dev/v2/quote", {
         headers: headers,
       });
       const quotes = rawQuotes.data;
       const quote = quotes.docs[Math.floor(Math.random() * quotes.docs.length)];
       setQuote(quote.dialog);
       console.log();
       const rawCharacters = await fetch(
         "https://the-one-api.dev/v2/character?_id=" + quote.character,
         { headers: headers }
       );
       const characters = await rawCharacters.json();
       const character = characters.docs[0];
       setCharacter(character.name);
     };

     fetchData();
   }, []);
  return (
    <div>
      <Link to="/character">
        <blockquote>{quote}</blockquote>
        <cite>- {character}</cite>
      </Link>
    </div>
  );
};

export default Quote
